function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }
var checkToday = new GlideAjax('NeedItWhenNeededToday');	
checkToday.addParam('sysparm_name','isDateToday');	
checkToday.addParam('sysparm_u_when_needed', g_form.getValue('u_when_needed'));
checkToday.getXML(chkTdy);

	function chkTdy(response){
	var flag = response.responseXML.documentElement.getAttribute('answer');

		if(flag == 'true'){
			alert("When needed can't be the same date as today, please change to a later date");
			g_form.clearValue('u_when_needed');
		}
	}
   //Type appropriate comment here, and begin script below

}
