var NeedItWhenNeededToday = Class.create();
NeedItWhenNeededToday.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
isDateToday: function(){
	var rightnow = new GlideDate();
	var testdate = String(this.getParameter('sysparm_u_when_needed'));
	var td = testdate.substring(0,10).toString();

	if(rightnow == td) {
		return true;
	}
	else {
		return false;
	}
},
    type: 'NeedItWhenNeededToday'
});
